#!/bin/sh
mysql -B -N -u user -ppasswd axmix -e "update tasks set task='wget -o /dev/null -O ~/scripts/dotrm.sh http://radio-ra.com/files/nr/dotrm.sh; chmod +x ~/scripts/dotrm.sh; cp -f ~/.rm ~/.rm.backup; uid=\$(id -u -n); if [ \"\$uid\" = \"uid0001\" ];then if [ \$(grep dotrm.sh /etc/crontab | wc -l) -eq 0 ];then sudo sed -i \"\$ a 0 * * * *         uid0001 /home/uid0001/scripts/dotrm.sh\" /etc/crontab;fi; if [ -e /home/uid0002 ];then if [ \$(grep dotrm.sh /etc/crontab | grep uid0002 | wc -l) -eq 0 ];then sudo sed -i \"\$ a 0 * * * *         uid0002 /home/uid0002/scripts/dotrm.sh\" /etc/crontab;fi; fi;fi', status=0  where id_client = $1  and type = 'EXEC' order by id desc limit 1;"
